import React, { Component } from 'react'
import Newsitem from './Newsitem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
export class News extends Component {
   static defaultProps = {
     country: 'in',
     pageSize: 8,
     category: 'general',
   }
   static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number
   }
   constructor(){
      super();
      console.log("hello i am a constructor");
      this.state={
        articles: [],
        loading: false,
        page:1

      }
   }
  async componentDidMount(){
    console.log("cdm");
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2&page=1&pageSize=${this.props.pageSize}`;
    this.setState({loading:true});
    try{
    let data = await fetch(url);
    let parsedData = await data.json();

    this.setState({articles: parsedData.articles, 
      totalResults: parsedData.totalResults,
      loading:false
    });
}
catch(e){
    console.log("something is not working");
}
}
handlePrevClick = async ()=>{
  console.log("Previous");
  let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
  this.setState({loading:true});
    try{
    let data = await fetch(url);
    let parsedData = await data.json();

    this.setState({ 
      page: this.state.page-1,
        articles: parsedData.articles, 
        loading:false
    });
    }
    catch(e){
      console.log("something is not working");
  }

}
handleNextClick = async ()=>{
  console.log("Next");
  if (!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
  let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
  this.setState({loading:true});
  try{
    let data = await fetch(url);
    let parsedData = await data.json();

    this.setState({ 
        page:this.state.page+1,
        articles: parsedData.articles,
        loading:false
    });
    }
    catch(e){
      console.log("something is not working");
  }
    }
}

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center" style={{margin:'35px 0px;'}}>NewsApp - Top headlines</h1>
        {this.state.loading && <Spinner/>}
         <div className="row">
            {this.state.articles.map((element)=>{

        return <div className="col-md-4" key={element.url}>    
         <Newsitem  title={element.title?element.title.slice(0, 45):""} description={element.description?element.description.slice(0, 88):""}  imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} />
            </div>
            })}
            </div>
            <div className="container d-flex justify-content-between">
                <button type="button" disabled={this.state.page<=1} className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
                <button type="button" disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr; </button>
            </div>
      </div>
    )
  }
}

export default News
