import React, { Component } from 'react'
import Newsitem from './Newsitem'

export class News extends Component {
   
   constructor(){
      super();
      console.log("hello i am a constructor");
      this.state={
        articles: [],
        loading: false,
        page:1
      }
   }
  async componentDidMount(){
    console.log("cdm");
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2";
    try{
    let data = await fetch(url);
    let parsedData = await data.json();

    this.setState({ 
        articles: parsedData.articles 
    });
}
catch(e){
    console.log("something is not working");
}
}
  render() {
    return (
      <div className="container my-3">
         <h2>NewsApp - Top headlines</h2>
         
         <div className="row">
            {this.state.articles.map((element)=>{

        return <div className="col-md-4" key={element.url}>    
         <Newsitem  title={element.title?element.title.slice(0, 45):""} description={element.description?element.description.slice(0, 88):""}  imageUrl={element.urlToImage} newsUrl={element.url} />
            </div>
            })}
            </div>
            <div className="container d-flex justify-content-between">
                <button type="button" className="btn btn-dark">Previous</button>
                <button type="button" className="btn btn-dark">Next</button>
            </div>
      </div>
    )
  }
}

export default News
