import React, { Component } from 'react'
import Newsitem from './Newsitem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
import InfiniteScroll from 'react-infinite-scroll-component';
export class News extends Component {
   static defaultProps = {
     country: 'in',
     pageSize: 8,
     category: 'general',
   }
   static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number
   }
   capitalizefirstLetter = (string)=> {
    return string.charAt(0).toUpperCase() + string.slice(1);
   }
   constructor(props){
      super(props);
      console.log("hello i am a constructor");
      this.state={
        articles: [],
        loading: false,
        page:1,
        totalResults:0
      }
      document.title=`${this.capitalizefirstLetter(this.props.category)} = NewsApp`;
   }
   async updateNews(pageNo){
   const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({loading:true});
    
    let data = await fetch(url);
    let parsedData = await data.json();

    this.setState({articles: parsedData.articles, 
      totalResults: parsedData.totalResults,
      loading:false
    });
   }
  
  async componentDidMount(){
    this.updateNews();
}

handlePrevClick = async ()=>{
  //console.log("Previous");
  //let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
  //this.setState({loading:true});
  //  try{
  //  let data = await fetch(url);
  //  let parsedData = await data.json();
//
  //  this.setState({ 
  //    page: this.state.page-1,
  //      articles: parsedData.articles, 
  //      loading:false
  //  });
  //  }
  //  catch(e){
  //    console.log("something is not working");
  //}
this.setState({page: this.state.page - 1});
this.updateNews();
}
handleNextClick = async ()=>{
  //console.log("Next");
  //if (!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
  //let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
  //this.setState({loading:true});
  //try{
  //  let data = await fetch(url);
  //  let parsedData = await data.json();
//
  //  this.setState({ 
  //      page:this.state.page+1,
  //      articles: parsedData.articles,
  //      loading:false
  //  });
  //  }
  //  catch(e){
  //    console.log("something is not working");
  //}
  //  }
  this.setState({page: this.state.page + 1});
  this.updateNews();
}
fetchMoreData = async () => {
  this.setState({ page: this.state.page + 1 })
  const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=24ccee62e29c4a7ea50e44c55f93c5b2&page=${this.state.page}&pageSize=${this.props.pageSize}`;
  let data = await fetch(url);
  let parsedData = await data.json()
  this.setState({
      articles: this.state.articles.concat(parsedData.articles),
      totalResults: parsedData.totalResults,
      loading: false,
  })
};

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center" style={{margin:'35px 0px'}}>NewsApp - Top {this.capitalizefirstLetter(this.props.category)} headlines</h1>
       {/* {this.state.loading && <Spinner/>}*/}
       <InfiniteScroll
       dataLength={this.state.articles.length}
       next={this.fetchMoreData}
       hasMore={this.state.articles.length !== this.state.totalResults}
       loader={<Spinner/>}
       ></InfiniteScroll>
       <div className="container"></div>
         <div className="row">
            {this.state.articles.map((element)=>{

        return <div className="col-md-4" key={element.url}>    
         <Newsitem  title={element.title?element.title.slice(0, 45):""} description={element.description?element.description.slice(0, 88):""}  imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
            </div>
            })}
            </div>
            <div className="container d-flex justify-content-between">
                <button type="button" disabled={this.state.page<=1} className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
                <button type="button" disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr; </button>
            </div>
      </div>
    )
  }
}

export default News
